import './Content.css'
import React from 'react';
import { useState } from 'react';
const Content = () =>{
    const [fontSize,setFontSize] = useState(50); 
    return(
        <div className="box">
            <h3>Welcome to ProfWriter, type or paste your paragraph to get a formal paragraph!</h3>
            <div className='tool'>
                <div className='io'>
                    <textarea className='in'/>
                    <button></button>
                </div>
                <div className='io'>
                    <h6>Formal Text</h6>
                </div>
            </div>
            <div>
                <button className='transform'>Transform</button>
            </div>
        </div>
    )
}

export default Content;
