import React,{useRef, useState} from "react";
import './Picture.css'; 

const Picture = () =>{
    const inputRef = useRef(); 
    const [image,setImage] = useState('');
    const handleImage = (event) => {
        const file = event.target.files[0]['name'];
        if (file) {
            const imageUrl = URL.createObjectURL(file);
            setImage(imageUrl);
        }
    };
    return(
        <div className="io">
            <div className="in">
                {image ? <img src={image} className="display"/> : <img src='./sample.jpg' alt="Sample image" className="display" />}
                <div>
                    <input className='picInput'type="file"  onChange={handleImage} ref={inputRef}/>
                    <button className="picButton">Analyze</button>
                </div>  
            </div>
            <div className="result">
                Result
            </div>
        </div>
    ); 
}

export default Picture;
