import './Content.css'
import React from 'react';
import { useState } from 'react';
import Text from './Text'
const Content = () =>{
    const [type,setType] = useState('text'); 
    const handleSetType = (newType) =>{
        setType(newType); 
    };
    return(
        <div className="box">
            <div className='tool'>
                <div className='tabs'>
                    <button className='tabButton' onClick={() => handleSetType('text')}>Text</button>
                    <button onClick={() => handleSetType('pic')}>Picture</button>
                    <button onClick={() => handleSetType('vid')}>Video</button>
                </div>
                <div className='content'>
                    {type === 'text' && <Text/>}
                    {type === 'pic' && <div>Picture Content</div>}
                    {type === 'vid' && <div>Video Content</div>}
                </div>
            </div>
        </div>
    )
}

export default Content;
